  `contact_firstname` varchar(255) DEFAULT NULL,
  `contact_lastname` varchar(255) DEFAULT NULL,
  `contact_mail` varchar(255) DEFAULT NULL,
  `contact_treated` tinyint DEFAULT NULL,
  `contact_date` date DEFAULT NULL,
  PRIMARY KEY (`contact_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

/**********************************************/
/* script pour créer la table description */
/**********************************************/

DROP TABLE IF EXISTS `description`;

CREATE TABLE `description` (
  `description_id` int NOT NULL AUTO_INCREMENT,
  `description_title1_fr` varchar(255) DEFAULT NULL,
  `description_title1_en` varchar(255) DEFAULT NULL,
  `description_title2_fr` varchar(255) DEFAULT NULL,
  `description_title2_en` varchar(255) DEFAULT NULL,
  `description_content_fr` text DEFAULT NULL,
  `description_content_en` text DEFAULT NULL,
  `description_photo` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`description_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

INSERT INTO `description` VALUES (1,'Qu\'est ce qu\'un Greeters ?','What\'s a greeter?','Rencontrez un greeter, votre ami en Touraine !','Meet a greeter, your friend in Touraine!','Le greeter, “hôte en anglais” est un habitant passionné par son territoire, sa ville, son village et accueille le visiteur lors d’une balade conviviale, authentique et instructive.  Il vous emmène autour de ses lieux préférés, vous offre ses « bons plans » et bonnes adresses … le tout dans une démarche entièrement basée sur le bénévolat ! Ces balades de 2 heures environ sont gratuites.\nUn Greeter n’est pas un guide. Les thèmes de balades sont variés et sont avant tout un prétexte pour une rencontre, une expérience humaine riche et un échange culturel qui restent à la base du rendez-vous.\nCe mouvement mondial, précurseur du tourisme participatif, est né aux États-Unis en 1992. Il rencontre un grand succès en France et dans le monde.\nLoire Valley Greeters est membre de la Fédération Française Greeters et Global Greeters Network et s\'engage à respecter la charte et les valeurs Greeters ','A greeter is a local resident who is passionate about his region, his town, his village and who welcomes  tourists with a friendly, authentic and enlightening stroll.\n\nHe will take you to his favourite places and share with you his good tips and addresses… And this, is all based on a voluntary initiative! These two-hour long strolls are free.\n\nA greeter is not a guide. The themes of the strolls are wide and it is mostly an opportunity for an enriching experience and a cultural exchange, which basically remains the base of the meeting.\n\nThis worldwide movement  is pionner of the participative tourism which was born in the United Sates of America in 1992. It has met a huge success in France and worldwide ever since.\n\nThe Loire Valley Greeters is member of both the Greeters French Federation and the Global Gretters Network, and respects the charter and its Greeters values.','http://localhost:3000/concept-banner.jpg');

